mapper.insert:
INSERT 
INTO FAQ
(
	Q_NO
  , Q_TITLE
  , Q_ANSWER
  , Q_ENROLL_DATE
  , Q_MODIFY_DATE
  , QC_NO
  , USER_NO
)
VALUES (
	SEQ_Q_NO.NEXTVAL
  ,	#{q_title}
  , #{q_answer}
  , SYSDATE
  , NULL
  , #{qc_no}
  , #{user_no}
)

mapper.update:
	UPDATE FAQ
	   SET 
	  	  Q_TITLE = #{q_title}
		, Q_ANSWER = #{q_answer}
		, Q_MODIFY_DATE = SYSDATE
    WHERE Q_NO = #{q_no}
